const isObject = val => typeof val === 'object' && val !== null

module.exports = deepassign

function deepassign (X, ...args) {
  for (var i = 0, len1 = args.length; i < len1; i++) {
    const val = args[i]
    const keys = Object.keys(val)
    for (var j = 0, len2 = keys.length; j < len2; j++) {
      const key = keys[j]
      X[key] = isObject(val[key]) ? deepassign(isObject(X[key]) ?
        X[key] : {}, val[key]) : val[key]
    }
  }
  return X
}