const bel = require('bel')
const csjs = require('csjs-inject')

module.exports = moreInfo

function moreInfo (network, txHash) {
  const css = classes
  var linkToEtherscan = "https://" + network  + ".etherscan.io/tx/" + txHash
  return bel`<div class=${css.infoIcon} title="Take me to the Etherscan">
    <a href=${linkToEtherscan} target="_blank"><i class="fa fa-info-circle"></i></a>
  </div>`
}
const classes = csjs`
.infoIcon {
  position: absolute;
  right: 5px;
  bottom: 5px;
}
.infoIcon a {
  font-size: 1.2em;
  text-decoration: none;
  color: #3E404E;
  opacity: 1;
  transition: color .6s, opacity .6s ease-in-out;
}
.infoIcon a:hover {
  color: white;
  opacity: 0.6;
}`